$(document).ready(function() {
	$('.pay_pal').mouseenter(function() {
		$(this).addClass("pay_opa");
	});
});
$(document).ready(function() {
	$('.pay_pal').mouseleave(function() {
		$(this).removeClass("pay_opa");
	});
});

